export class taskMaster{constructor(){this.tasklist=[],this.load()}createHtmlCard(t){document.getElementById("task_list").innerHTML+=`\n\n    <div  class="card text-white bg-primary">\n      <div class="card-body">\n      \n         \n        <h5 class="card-header">Task name: ${t.task}</h5>\n        <div class="card-body">\n        <p class="card-text">Description: ${t.description}</p>\n          <p class="card-text">due date: ${t.dueDate}</p>\n          <p class="card-text">Assigned to: ${t.assignedTo}</p> \n          <p class="card-text">Status: ${t.status}</p>\n\n          <div class="options">  \n          <i onclick="taskMaster.editTask(${t.id})" data-bs-toggle="modal" data-bs-target="#form" class="fas fa-edit"></i>\n          <i onclick="deleteTask(${t.id})" class="fas fa-trash-alt"></i>\n          <i onClick ="alertUser();" class="fas fa-check"></i>\n\n          </div>\n        </div>\n      </div>\n            </div>\n\n    `}render(){this.tasklist.forEach(t=>this.createHtmlCard(t))}addTask(t){this.tasklist.push(t),this.save()}save(){localStorage.setItem("tasklist",JSON.stringify(this.tasklist))}load(){this.tasklist=JSON.parse(localStorage.getItem("tasklist")),null==this.tasklist&&(this.tasklist=[])}getAllTasks(){return this.tasklist}getTasksWithStatus(t){this.tasklist.filter(s=>s.status===t),"Done"==t&&(document.getElementById("(${task.id}icon").style.display="none")}updateTask(t){let s=this.tasklist.find(s=>s.id===t);s.status="Done",this.save(),location.reload()}alertUser(){alert("Task has been added")}editTask(t){let s=this.tasklist.find(s=>s.id===t);document.getElementById("name-task-input").value=s.task,document.getElementById("description-task-input").value=s.description,document.getElementById("assigned-to-task-input").value=s.assignedTo,document.getElementById("due-date-task-input").value=s.dueDate,document.getElementById("status-task-input").value=s.status,document.getElementById("id-task-input").value=s.id,document.getElementById("Modal-label1").innerHTML="Edit Task",document.getElementById("submit").innerHTML="Edit Task",document.getElementById("submit").onclick=function(){taskMaster.updateTask(t)}}}